var link_tabs=document.getElementsByClassName("link-tab"),item_tabs=document.getElementsByClassName("item-tab");for(e=0;e<link_tabs.length;e++)link_tabs[e].addEventListener("click",function(t){t.preventDefault(),displayTab(this.getAttribute("data-item")),activeMenu(),this.classList.add("active")},!1);function displayTab(t){for(i=0;i<item_tabs.length;i++)item_tabs[i].classList.remove("active");document.getElementById(t).classList.add("active")}function activeMenu(){for(a=0;a<link_tabs.length;a++)link_tabs[a].classList.remove("active")}